    private void payActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_payActionPerformed
       if (jCheckBox1.isSelected()==true)
               pay.setEnabled(true);
       int id= Integer.parseInt(vid.getText());
       try
    {

        Class.forName("java.sql.Driver");
        Connection con=DriverManager.getConnection("jdbc:mysql://localhost:3306/hotel_management", "root", "root");
        Statement stmt=con.createStatement();
        String query1="SELECT Bill FROM visitors where Vis_id="+id+";";
       ResultSet rs=stmt.executeQuery(query1);
       rs.first();
       String result= rs.getString("Bill");
      amt.setText(result);
     }
    catch(Exception e)
    {
            JOptionPane.showMessageDialog(null,e.getMessage());
        }
    }//GEN-LAST:event_payActionPerformed

